#!/bin/sh
#loop through a list of server names in "serverlist.txt" spit out arcsight wuc formated output for importing in arcmc
#incomplete list as is, just wanted to get htis down for future use
( ./getOS.sh ./serverlist.txt ) >test2.txt 2>&1 && egrep -A1 ^'\[' ./test2.txt | egrep -v ^-- | sed 's/^Domain.*OS=//g;s/ Server=.*]//g' | tr -d '\n' | sed 's/\[//g;s/]/\n/g' |rev|cut -d',' -f -2|rev | sed 's/Windows Server 2008 R2 Enterprise 7601 Service Pack 1/,,TRUE,FALSE,FALSE,,Windows Server 2008 R2,en_US/g;s/Windows Server 2008 R2 Standard 7601 Service Pack 1/,,TRUE,FALSE,FALSE,,Windows Server 2008 R2,en_US/g;s/Windows Server 2012 R2 Datacenter 9600/,,TRUE,FALSE,FALSE,,Windows Server 2012 R2,en_US/g;s/Windows Server 2012 R2 Standard 9600/,,TRUE,FALSE,FALSE,,Windows Server 2012 R2,en_US/g;s/Windows Server (R) 2008 Enterprise 6002 Service Pack 2/,,TRUE,FALSE,FALSE,,Windows Server 2008,en_US/g;s/Windows Server (R) 2008 Standard 6002 Service Pack 2/,,TRUE,FALSE,FALSE,,Windows Server 2008,en_US/g;s/Windows Server (R) 2008 Standard without Hyper-V 6002 Service Pack 2/,,TRUE,FALSE,FALSE,,Windows Server 2008,en_US/g;s/^/,/'
